import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import jwt_decode from 'jwt-decode'; // Pastikan Anda menginstal jwt-decode [cite: 278]

const Navbar = () => {
    const navigate = useNavigate();
    const [user, setUser] = useState(null);

    useEffect(() => {
        const token = localStorage.getItem('token');
        if (token) {
            try {
                // Gunakan jwt-decode untuk mendapatkan payload pengguna [cite: 278]
                const decoded = jwt_decode(token);
                setUser(decoded);
            } catch (error) {
                console.error("Token decoding failed:", error);
                handleLogout(); // Hapus token jika rusak
            }
        } else {
            setUser(null);
        }
    }, [navigate]);

    const handleLogout = () => {
        localStorage.removeItem('token'); // Hapus token [cite: 280]
        setUser(null);
        navigate('/login'); // Navigasi ke /login [cite: 280]
    };

    // --- Styling Sederhana ---
    const navStyle = {
        backgroundColor: '#800020', // Warna Burgundy (dari modifikasi sebelumnya)
        padding: '15px 50px',
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center',
        color: 'white',
        boxShadow: '0 2px 5px rgba(0,0,0,0.1)',
    };
    const linkStyle = { color: 'white', textDecoration: 'none', margin: '0 15px', fontWeight: 'bold' };
    const userInfoStyle = { display: 'flex', alignItems: 'center' };
    const logoutButtonStyle = { background: 'none', border: 'none', color: '#ffc107', cursor: 'pointer', fontWeight: 'bold', marginLeft: '20px' };

    return (
        <nav style={navStyle}>
            {/* Logo dan Link Utama */}
            <div style={{ display: 'flex', alignItems: 'center' }}>
                <Link to="/dashboard" style={{...linkStyle, fontSize: '1.2em'}}>
                    ACADEMIC PRESENCE
                </Link>
                <Link to="/presensi" style={linkStyle}>
                    Presensi
                </Link>
                {/* Tampilkan menu "Laporan Admin" hanya jika role = 'admin' [cite: 279] */}
                {user && user.role === 'admin' && (
                    <Link to="/reports" style={linkStyle}>
                        Laporan Admin
                    </Link>
                )}
            </div>

            {/* Status Pengguna dan Logout */}
            <div style={userInfoStyle}>
                {user ? (
                    <>
                        <span>Halo, {user.nama} ({user.role})</span> {/* Tampilkan user.nama dari token [cite: 278] */}
                        <button onClick={handleLogout} style={logoutButtonStyle}>
                            Logout
                        </button>
                    </>
                ) : (
                    <Link to="/login" style={linkStyle}>Login / Register</Link>
                )}
            </div>
        </nav>
    );
};

export default Navbar;
