import React from 'react';

const DashboardPage = () => {
    // Ambil token dan data pengguna dari Local Storage
    const token = localStorage.getItem('token');
    // Anda harus mengurai (parse) token untuk mendapatkan nama pengguna, tapi kita gunakan placeholder dulu
    const userName = 'Mahasiswa Awal'; 
    const userRole = 'mahasiswa';

    const handleLogout = () => {
        localStorage.removeItem('token');
        // Redirect ke halaman Login (menggunakan navigasi di App.js)
        window.location.href = '/login'; 
    };

    // --- STYLING ESTETIK BARU ---
    const primaryColor = '#3b82f6';
    const accentColor = '#60a5fa';

    const containerStyle = {
        backgroundColor: '#f4f7f6',
        minHeight: '100vh',
        padding: '30px 50px',
    };

    const headerStyle = {
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center',
        paddingBottom: '20px',
        borderBottom: '1px solid #e5e7eb',
        marginBottom: '30px',
    };

    const welcomeCardStyle = {
        background: `linear-gradient(90deg, ${primaryColor} 0%, ${accentColor} 100%)`,
        borderRadius: '15px',
        padding: '40px',
        color: 'white',
        boxShadow: '0 8px 15px rgba(0, 0, 0, 0.1)',
        marginBottom: '30px',
    };

    const metricCardStyle = {
        backgroundColor: 'white',
        borderRadius: '10px',
        padding: '25px',
        boxShadow: '0 4px 8px rgba(0, 0, 0, 0.05)',
    };
    
    // Grid untuk 3 kartu metrik
    const metricsGridStyle = {
        display: 'grid',
        gridTemplateColumns: 'repeat(auto-fit, minmax(280px, 1fr))',
        gap: '20px',
        marginBottom: '40px',
    };

    if (!token) {
        return (
            <div style={{...containerStyle, textAlign: 'center', paddingTop: '100px'}}>
                <h2 style={{color: '#ef4444'}}>Akses Ditolak</h2>
                <p>Anda belum login. Silakan <a href="/login">Login disini</a>.</p>
            </div>
        );
    }

    return (
        <div style={containerStyle}>
            <div style={headerStyle}>
                <h1 style={{ fontSize: '24px', fontWeight: 'bold', color: '#1f2937' }}>
                    <span role="img" aria-label="dashboard">ðŸ“Š</span> Dashboard Akademik
                </h1>
                <div style={{ display: 'flex', alignItems: 'center', fontSize: '14px', color: '#6b7280' }}>
                    <span style={{ marginRight: '15px' }}>{new Date().toLocaleTimeString('id-ID', { hour: '2-digit', minute: '2-digit', hour12: false })} WIB</span>
                    <button onClick={handleLogout} style={{ background: 'none', border: 'none', color: '#ef4444', cursor: 'pointer', fontWeight: '600' }}>
                        â†’ Logout
                    </button>
                </div>
            </div>

            <div style={welcomeCardStyle}>
                <p style={{ fontSize: '18px', opacity: 0.8 }}>Selamat Datang Kembali,</p>
                <h2 style={{ fontSize: '36px', fontWeight: '900', marginTop: '5px' }}>{userName}</h2>
                <p style={{ fontSize: '14px', opacity: 0.9 }}>Akses cepat ringkasan informasi akademik Anda di sini.</p>
            </div>

            <div style={metricsGridStyle}>
                {/* Kartu 1: SKS */}
                <div style={{...metricCardStyle, borderLeft: `5px solid #60a5fa`}}>
                    <p style={{ color: '#6b7280', fontSize: '14px', marginBottom: '10px' }}>Mata Kuliah Diambil</p>
                    <h3 style={{ fontSize: '32px', fontWeight: 'bold', color: '#1f2937' }}>8 SKS</h3>
                    <a href="#" style={{ color: primaryColor, textDecoration: 'none', fontSize: '14px', marginTop: '10px', display: 'block' }}>Aksi Cepat &gt;</a>
                </div>
                
                {/* Kartu 2: Pertemuan */}
                <div style={{...metricCardStyle, borderLeft: `5px solid #34d399`}}>
                    <p style={{ color: '#6b7280', fontSize: '14px', marginBottom: '10px' }}>Jadwal Minggu Ini</p>
                    <h3 style={{ fontSize: '32px', fontWeight: 'bold', color: '#1f2937' }}>12 Pertemuan</h3>
                    <a href="#" style={{ color: '#34d399', textDecoration: 'none', fontSize: '14px', marginTop: '10px', display: 'block' }}>Aksi Cepat &gt;</a>
                </div>

                {/* Kartu 3: IPK */}
                <div style={{...metricCardStyle, borderLeft: `5px solid #f97316`}}>
                    <p style={{ color: '#6b7280', fontSize: '14px', marginBottom: '10px' }}>IPK Sementara</p>
                    <h3 style={{ fontSize: '32px', fontWeight: 'bold', color: '#1f2937' }}>3.75</h3>
                    <a href="#" style={{ color: '#f97316', textDecoration: 'none', fontSize: '14px', marginTop: '10px', display: 'block' }}>Aksi Cepat &gt;</a>
                </div>
            </div>

            {/* Pemberitahuan Penting */}
            <div style={{ backgroundColor: 'white', borderRadius: '10px', padding: '25px', boxShadow: '0 4px 8px rgba(0, 0, 0, 0.05)' }}>
                <h3 style={{ fontSize: '18px', fontWeight: 'bold', marginBottom: '15px' }}>Pemberitahuan Penting</h3>
                <ul style={{ listStyleType: 'none', padding: 0 }}>
                    <li style={{ marginBottom: '10px', display: 'flex', alignItems: 'center' }}>
                        <span style={{ color: '#ef4444', marginRight: '10px', fontSize: '18px' }}>â€¢</span>
                        Batas Akhir pengisian KRS adalah tanggal 25 Nov 2025.
                    </li>
                    <li style={{ marginBottom: '10px', display: 'flex', alignItems: 'center' }}>
                        <span style={{ color: '#facc15', marginRight: '10px', fontSize: '18px' }}>â€¢</span>
                        Pengumuman Beasiswa Genap 2025 telah dirilis.
                    </li>
                </ul>
            </div>
        </div>
    );
};

export default DashboardPage;
