import React, { useState } from 'react';

const LoginPage = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [message, setMessage] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();
        setMessage('Sedang memproses...');
        
        try {
            // Ganti dengan URL backend Anda (sesuai server.js)
            const response = await fetch('http://localhost:3001/api/auth/login', { 
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ email, password })
            });

            const data = await response.json();

            if (response.ok) {
                // Simpan Token JWT
                localStorage.setItem('token', data.token); 
                setMessage(`Login Berhasil! Token disimpan.`);
                // Anda bisa tambahkan navigasi ke Dashboard di sini
            } else {
                setMessage(`Login Gagal: ${data.message}`);
            }
        } catch (error) {
            setMessage('Terjadi kesalahan koneksi.');
            console.error('Error saat login:', error);
        }
    };

    return (
        <div>
            <h2>Halaman Login</h2>
            <form onSubmit={handleSubmit}>
                <div>
                    <label>Email:</label>
                    <input 
                        type="email" 
                        value={email} 
                        onChange={(e) => setEmail(e.target.value)} 
                        required 
                    />
                </div>
                <div>
                    <label>Password:</label>
                    <input 
                        type="password" 
                        value={password} 
                        onChange={(e) => setPassword(e.target.value)} 
                        required 
                    />
                </div>
                <button type="submit">Login</button>
            </form>
            <p style={{ color: message.includes('Gagal') ? 'red' : 'green' }}>{message}</p>
        </div>
    );
};

export default LoginPage;
