import React, { useState } from 'react';

const RegisterPage = () => {
    const [nama, setNama] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [message, setMessage] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();
        setMessage('Sedang mendaftarkan...');
        
        try {
            const response = await fetch('http://localhost:3001/api/auth/register', { 
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ nama, email, password, role: 'mahasiswa' })
            });

            const data = await response.json();

            if (response.ok) {
                setMessage(`Registrasi Berhasil! ID: ${data.data.id}. Silakan Login.`);
            } else {
                setMessage(`Registrasi Gagal: ${data.message}`);
            }
        } catch (error) {
            setMessage('Terjadi kesalahan koneksi.');
            console.error('Error saat registrasi:', error);
        }
    };

    const containerStyle = {
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'center',
        minHeight: '80vh',
        backgroundColor: '#f4f7f6',
    };

    const formBoxStyle = {
        padding: '40px',
        borderRadius: '8px',
        boxShadow: '0 4px 12px rgba(0, 0, 0, 0.1)',
        backgroundColor: 'white',
        width: '350px',
    };

    const inputStyle = {
        width: '100%',
        padding: '10px',
        margin: '8px 0 20px 0',
        display: 'inline-block',
        border: '1px solid #ccc',
        borderRadius: '4px',
        boxSizing: 'border-box',
    };

    const buttonStyle = {
        backgroundColor: '#28a745', // Hijau untuk register
        color: 'white',
        padding: '12px 20px',
        border: 'none',
        borderRadius: '4px',
        cursor: 'pointer',
        width: '100%',
        fontSize: '16px',
        marginTop: '10px',
    };

    return (
        <div style={containerStyle}>
            <div style={formBoxStyle}>
                <h2>✍️ Halaman Registrasi</h2>
                <form onSubmit={handleSubmit}>
                    <div>
                        <label>Nama:</label>
                        <input 
                            type="text" 
                            value={nama} 
                            onChange={(e) => setNama(e.target.value)} 
                            style={inputStyle}
                            required 
                        />
                    </div>
                    <div>
                        <label>Email:</label>
                        <input 
                            type="email" 
                            value={email} 
                            onChange={(e) => setEmail(e.target.value)} 
                            style={inputStyle}
                            required 
                        />
                    </div>
                    <div>
                        <label>Password:</label>
                        <input 
                            type="password" 
                            value={password} 
                            onChange={(e) => setPassword(e.target.value)} 
                            style={inputStyle}
                            required 
                        />
                    </div>
                    <button type="submit" style={buttonStyle}>Daftar</button>
                </form>
                <p style={{ color: message.includes('Gagal') ? 'red' : 'green', marginTop: '15px' }}>{message}</p>
            </div>
        </div>
    );
};

export default RegisterPage;
