import React from 'react';

const DashboardPage = () => {
    // Ambil token dari Local Storage
    const token = localStorage.getItem('token');

    const handleLogout = () => {
        localStorage.removeItem('token');
        alert('Anda telah log out.');
        // Anda bisa tambahkan navigasi ke halaman Login di sini
    };

    const containerStyle = {
        padding: '40px',
        borderRadius: '8px',
        boxShadow: '0 4px 12px rgba(0, 0, 0, 0.1)',
        backgroundColor: 'white',
        margin: '40px auto',
        maxWidth: '600px',
        textAlign: 'center',
    };

    const buttonStyle = {
        backgroundColor: '#dc3545', // Merah untuk Logout
        color: 'white',
        padding: '10px 20px',
        border: 'none',
        borderRadius: '4px',
        cursor: 'pointer',
        fontSize: '16px',
        marginTop: '20px',
    };

    return (
        <div style={{ backgroundColor: '#f4f7f6', minHeight: '90vh' }}>
            <div style={containerStyle}>
                <h2>üìä Dashboard Aplikasi</h2>
                {token ? (
                    <>
                        <p style={{ color: '#28a745', fontWeight: 'bold' }}>‚úÖ Selamat datang! Anda telah terautentikasi.</p>
                        <p>Ini adalah halaman yang dilindungi. Data token Anda:</p>
                        <div style={{ wordBreak: 'break-all', fontSize: '12px', padding: '10px', border: '1px solid #ccc', borderRadius: '4px', margin: '15px 0' }}>
                            Token: {token.substring(0, 70)}...
                        </div>
                        <button onClick={handleLogout} style={buttonStyle}>Logout</button>
                    </>
                ) : (
                    <p style={{ color: '#ffc107', fontWeight: 'bold' }}>‚ö†Ô∏è Anda belum login. Silakan kembali ke halaman Login untuk mendapatkan akses.</p>
                )}
            </div>
        </div>
    );
};

export default DashboardPage;
