import React, { useState } from 'react';

const RegisterPage = () => {
    const [nama, setNama] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [message, setMessage] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();
        setMessage('Sedang mendaftarkan...');
        
        try {
            // Ganti dengan URL backend Anda (sesuai server.js)
            const response = await fetch('http://localhost:3001/api/auth/register', { 
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ nama, email, password, role: 'mahasiswa' }) // Role default mahasiswa
            });

            const data = await response.json();

            if (response.ok) {
                setMessage(`Registrasi Berhasil! ID: ${data.data.id}`);
            } else {
                setMessage(`Registrasi Gagal: ${data.message}`);
            }
        } catch (error) {
            setMessage('Terjadi kesalahan koneksi.');
            console.error('Error saat registrasi:', error);
        }
    };

    return (
        <div>
            <h2>Halaman Registrasi</h2>
            <form onSubmit={handleSubmit}>
                <div>
                    <label>Nama:</label>
                    <input 
                        type="text" 
                        value={nama} 
                        onChange={(e) => setNama(e.target.value)} 
                        required 
                    />
                </div>
                <div>
                    <label>Email:</label>
                    <input 
                        type="email" 
                        value={email} 
                        onChange={(e) => setEmail(e.target.value)} 
                        required 
                    />
                </div>
                <div>
                    <label>Password:</label>
                    <input 
                        type="password" 
                        value={password} 
                        onChange={(e) => setPassword(e.target.value)} 
                        required 
                    />
                </div>
                <button type="submit">Daftar</button>
            </form>
            <p style={{ color: message.includes('Gagal') ? 'red' : 'green' }}>{message}</p>
        </div>
    );
};

export default RegisterPage;
