import React, { useState } from 'react';
// ... (Styling variables dan utilitas sama seperti LoginPage)

const RegisterPage = () => {
    // State dan fungsi handleSubmit sama seperti sebelumnya...
    const [nama, setNama] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [role, setRole] = useState('mahasiswa');
    const [message, setMessage] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();
        setMessage('Sedang mendaftarkan...');
        // ... Logika Fetch API sama seperti sebelumnya (POST /api/auth/register) ...
        try {
            const response = await fetch('http://localhost:3001/api/auth/register', { 
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ nama, email, password, role })
            });
            const data = await response.json();
            if (response.ok) {
                setMessage('Registrasi Berhasil! Silakan Login.');
            } else {
                setMessage(`Registrasi Gagal: ${data.message}`);
            }
        } catch (error) {
            setMessage('Terjadi kesalahan koneksi.');
        }
    };

    // --- STYLING ESTETIK BARU ---
    const primaryColor = '#3b82f6'; 
    const bgColor = '#1e3a8a'; 
    
    const containerStyle = {
        minHeight: '100vh',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        background: `linear-gradient(135deg, ${bgColor} 0%, #172554 100%)`,
    };

    const formBoxStyle = {
        padding: '50px 40px',
        borderRadius: '20px', 
        boxShadow: '0 10px 30px rgba(0, 0, 0, 0.25)',
        backgroundColor: 'white',
        width: '100%',
        maxWidth: '400px',
        textAlign: 'center',
    };

    const inputGroupStyle = {
        textAlign: 'left',
        marginBottom: '20px',
    };

    const inputStyle = {
        width: '100%',
        padding: '12px',
        marginTop: '8px',
        border: '1px solid #e5e7eb',
        borderRadius: '10px',
        boxSizing: 'border-box',
        fontSize: '16px',
        // Untuk select
        appearance: 'none', 
    };

    const buttonStyle = {
        background: primaryColor,
        color: 'white',
        padding: '15px 20px',
        border: 'none',
        borderRadius: '10px',
        cursor: 'pointer',
        width: '100%',
        fontSize: '18px',
        fontWeight: 'bold',
        marginTop: '20px',
    };

    return (
        <div style={containerStyle}>
            <div style={formBoxStyle}>
                <h2 style={{ fontSize: '24px', fontWeight: 'bold', color: '#1f2937' }}>Buat Akun Baru</h2>
                <p style={{ color: '#6b7280', marginBottom: '30px' }}>Daftar untuk mengakses sistem informasi akademik</p>
                
                <form onSubmit={handleSubmit}>
                    {/* Nama */}
                    <div style={inputGroupStyle}>
                        <label style={{ display: 'block', fontWeight: '600', marginBottom: '5px', color: '#4b5563' }}>Nama Lengkap</label>
                        <input 
                            type="text" 
                            value={nama} 
                            onChange={(e) => setNama(e.target.value)} 
                            style={inputStyle}
                            placeholder="Nama sesuai KTP/NIM"
                            required 
                        />
                    </div>
                    {/* Email */}
                    <div style={inputGroupStyle}>
                        <label style={{ display: 'block', fontWeight: '600', marginBottom: '5px', color: '#4b5563' }}>Email</label>
                        <input 
                            type="email" 
                            value={email} 
                            onChange={(e) => setEmail(e.target.value)} 
                            style={inputStyle}
                            placeholder="email@universitas.ac.id"
                            required 
                        />
                    </div>
                    {/* Password */}
                    <div style={inputGroupStyle}>
                        <label style={{ display: 'block', fontWeight: '600', marginBottom: '5px', color: '#4b5563' }}>Password</label>
                        <input 
                            type="password" 
                            value={password} 
                            onChange={(e) => setPassword(e.target.value)} 
                            style={inputStyle}
                            placeholder="Buat password yang kuat"
                            required 
                        />
                    </div>
                    {/* Role */}
                    <div style={inputGroupStyle}>
                        <label style={{ display: 'block', fontWeight: '600', marginBottom: '5px', color: '#4b5563' }}>Role</label>
                        <div style={{ position: 'relative' }}>
                            <select 
                                value={role} 
                                onChange={(e) => setRole(e.target.value)} 
                                style={inputStyle}
                            >
                                <option value="mahasiswa">Mahasiswa</option>
                                <option value="admin">Admin</option>
                            </select>
                        </div>
                    </div>

                    <button type="submit" style={buttonStyle}>Daftar Sekarang</button>
                </form>

                <p style={{ marginTop: '30px', color: '#6b7280' }}>
                    Sudah punya akun? 
                    <a href="/login" style={{ color: primaryColor, textDecoration: 'none', fontWeight: '600' }}> Login disini</a>
                </p>
                <p style={{ color: message.includes('Gagal') ? '#ef4444' : primaryColor, marginTop: '15px', fontWeight: '600' }}>{message}</p>
            </div>
        </div>
    );
};

export default RegisterPage;
