import React from 'react';

const DashboardPage = () => {
    // Ambil token dari Local Storage
    const token = localStorage.getItem('token');

    const handleLogout = () => {
        localStorage.removeItem('token');
        // Anda bisa tambahkan navigasi ke halaman Login di sini
        alert('Anda telah log out.');
    };

    return (
        <div>
            <h2>Dashboard Aplikasi</h2>
            {token ? (
                <>
                    <p>Selamat datang! Anda telah terautentikasi.</p>
                    <p>Token Anda: {token.substring(0, 30)}...</p> 
                    <button onClick={handleLogout}>Logout</button>
                    {/* Di sini tempat Anda menampilkan data yang membutuhkan JWT */}
                </>
            ) : (
                <p style={{ color: 'red' }}>Anda belum login. Silakan kembali ke halaman Login.</p>
            )}
        </div>
    );
};

export default DashboardPage;
